.rad-shadow {
  border: 1px solid hsl(var(--brand-hue) 10% 80% / 80%);
  box-shadow: 0 2rem 1.5rem 1.5rem;
  box-shadow: 0 2.8px 2.2px hsl(var(--surface-shadow) / calc(var(--shadow-strength) + 0.9)),
