/* =================================================================
 * Definições Personalizadas de Campos de Entrada
 * ================================================================= 
 * 
 */

input {
        /* accent-color: var(--yellow); */
}

input[type="color"] {
        -webkit-appearance: none;
        outline: 5px solid var(--surface2);
        border: none;
        width: 32px;
        height: 32px;
}
input[type="color"]::-webkit-color-swatch-wrapper {
        padding: 0;
}
input[type="color"]::-webkit-color-swatch {
        border: none;
}

input[type="radio"] {
    background-color: var(--surface1);
    border-radius: 1rem;
    box-shadow: inset 0 1px 5px hsla(0,0%,100%,.8),
                0 0 0 1px hsl(var(--surface-shadow) / calc(var(--shadow-strength) + 0.6)),
                0 2px 3px hsl(var(--surface-shadow) / calc(var(--shadow-strength) + 0.6)),
                0 4px 3px hsl(var(--surface-shadow) / calc(var(--shadow-strength) + 0.4)),
                0 6px 6px hsl(var(--surface-shadow) / calc(var(--shadow-strength) + 0.2)),
                0 10px 6px hsl(var(--surface-shadow) / calc(var(--shadow-strength) + 0.2));
    cursor: pointer;
    display: inline-block;
    height: 15px;
    margin-right: 15px;
    position: relative;
    width: 15px;
    -webkit-appearance: none;
}
input[type="radio"]:after {
    background-color: var(--dark);
    border-radius: 25px;
    box-shadow: inset 0 0 0 1px hsla(0,0%,0%,.4),
                0 1px 1px hsla(0,0%,100%,.8);
    content: '';
    display: block;
    height: 7px;
    left: 4px;
    position: relative;
    top: 4px;
    width: 7px;
}
input[type="radio"]:checked:after {
    background-color: var(--light);
    box-shadow: inset 0 0 0 1px hsla(0,0%,0%,.5),
                inset 0 2px 2px hsla(0,0%,100%,.4),
                0 1px 1px hsla(0,0%,100%,.8),
                0 0 2px 2px hsla(0,70%,70%,.4);
}

